int strMatchKMP(char mainStr[], char subStr[])
{
    // KMPæ³•è¿›è¡Œå­—ç¬¦ä¸²åŒ¹é…
    // è¿”å›å­ä¸²subSträ¸ä¸»ä¸²mainStrç›¸åŒ¹é…çš„ç¬¬ä¸€ä¸ªä½ç½®

    int * next;
    int lenSS,lenMS;
    int i,j;
    lenSS = lengthStr(subStr);
    next = (int *)malloc(sizeof (int ) * lenSS);
    computeNext(subStr,next);
    lenMS = lengthStr(mainStr);
    i = 0;
    j = 0;
    while (i<lenMS-lenSS+1){
        while (j<lenSS)
        {
            if (mainStr[i] == subStr[j]){
                i++;
                j++;
            } else{
                j = next[j];
                if (j == -1) {j = 0;i = i+1;}
            }
        }
        return i-j;
    }
    return -1;
}
