#include <iostream>
#include <string>
using namespace std;

string getMinString(string str1, string str2) {
    // write code here
    if (str2.empty()) { //修改1
        return str1;
    }
    string ans;
    int maxlen = 0; //最大匹配字符长度

    for (int i = 0; i < str1.length(); i++) {
        int j = 0;
        for (j = 0; j < str2.length(); j++) {
            //修改
            if (str1[i + j] == str2[j])
                continue;
            else {
                break;
            }
        }
        //如果找到str2的所有字符，直接返回str2就可以了
        if (j == str2.length())
            return str2;
        if (j > maxlen) {
            maxlen = j;
            ans = str1.substr(i, maxlen);
        }
    }

    if (maxlen == 0)
        return "";
    else
        return ans;
}

int main()
{
    string s1 = "te123st123";
    string s2 = "test";
    cout << getMinString(s1, s2);
    return 0;
}

