    int m=strlen(tar);
    //先统一转小写
    for(int i=0;i<n;i++){
        if(art[i]>='A'&&art[i]<='Z')art[i]+=32;
    }
    for(int i=0;i<m;i++){
        if(tar[i]>='A'&&tar[i]<='Z')tar[i]+=32;
    }
    int f=-1,count=0;
    int j,k;
    for(int i=0;i<n;i++){
        //不是第一个字母或前面没空格，不用比较
        if(i!=0&&art[i-1]!=' ')continue;
        j=0;
        k=i;
        while(tar[j++]==art[k++]&&j<m){}
        //找到相同的，并且后面是空格或者是最后一个字母，则是一样的单词
        if(j==m && (art[k]==' '||k==n)){
            if(f==-1)f=i;
            count++;
            i=k-1;
        }
    }
    if(count==0)printf("-1");
    else printf("%d %d",count,f);
    return 0;
}

