#include<stdio.h>
#include<string.h>
int main(){
    int t=0,p=-1,i=0,n=0;
    char s1[1000],s2[1000],c;
    scanf("%s",s1);
    while(getchar()!='\n');
    for(i=0;i<strlen(s1);i++){
        if(s1[i]>='a' && s1[i]<='z')s1[i]-='a'-'A';
    }
    s1[i]='\0';
    do{
        c=getchar();
        n++;
        if(c>='a' && c<='z')c-='a'-'A';
        if(c==' '||c=='\n'){
            s2[i++]='\0';
            if(strcmp(s1,s2)==0){
                if(p==-1)p=n-i;
                t++;
            }
            i=0;
        }else{
            s2[i++]=c;
        }
    }while(c!='\n');
    if(t>0){
        printf("%d %d",t,p);
    }else{
        printf("%d",-1);
    }
    return 0;
}

