#include<iostream>
using namespace std;
void func(double* p)
{
    for (int i = 0; i < 9; i++)
    {
        for (int j = 0; j < 9 - i; j++)
        {
            double temp;
            temp = p[j];
            p[j] = p[j + 1];
            p[j + 1] = temp;
        }
    }
}
double js(double* p)
{
    double sum = 0;
    for (int i = 2; i < 8; i++)
    {
        sum += p[i];
    }
    return sum / 6.0;
}

double ys(double* p)
{
    double sum = 0;
    for (int i = 1; i < 9; i++)
    {
        sum += p[i];
    }
    return sum / 8.0;
}

