{
    double* p = new double[10];
    for (int i = 0; i < 10; i++)
    {
        cin >> p[i];
    }
    int x;
    cin >> x;
    if (x == 1) {
        cout << ys(p);
    }
    if (x == 2) {
        cout << js(p);
    }
    delete[]p;
    return 0;
}

