public static void main(String[] args) {
    int n = 0x27A5F;
    boolean flag = (n >>> 9 & 1) == 1;
    int n1 = n & 0xFFFF;
    int n2 = n | (1 << 5);
    int n3 = n & (~(1 << 4));

    System.out.println("flag:" + flag);
    System.out.println("n1:" + Integer.toHexString(n1));
    System.out.println("n2:" + Integer.toHexString(n2));
    System.out.println("n3:" + Integer.toHexString(n3));

    System.out.println("以下二进制输出，方便验证题目要求");
