            {
                ps[n].type = e_student;
                printf("请输入学号：");
                scanf("%s",ps[n].id);
                break;
            }else if(t == 2)
            {
                ps[n].type = e_teacher;
                printf("请输入教职工号：");
                scanf("%s",ps[n].id);
                break;
            }else if(t == 3)
            {
