        {
            printf("是否继续录入(1.是 0.否)：");
            scanf("%d",&flag);
            if(flag == 0 || flag == 1)
                break;
        }
        if(flag == 0)
            break;

    }
    save(ps,n); //保存信息
    return n;
}

