//比较时间
int cmp(Date d1,Date d2)
{
    if(d1.y < d2.y)
        return -1;
    else if(d1.y > d2.y)
        return 1;
    else
    {
        if(d1.m < d2.m)
            return -1;
        else if(d1.m > d2.m)
            return 1;
        else
        {
            if(d1.d < d2.d)
                return -1;
            else if(d1.d > d2.d)
                return 1;
            else
                return 0;
        }
    }
}


//9.排序
void sortByName(Person ps[],int n)
{
    int i,j;
    Person t;
    for(i=0;i<n-1;i++)
    {
        for(j=0;j<n-1-i;j++)
        {
            if(strcmp(ps[j].name,ps[j+1].name)> 0)
            {
                t = ps[j];
                ps[j] = ps[j+1];
                ps[j+1] = t;
            }else if(strcmp(ps[j].name,ps[j+1].name)== 0)
            {
                if(ps[j].op == IN_SCHOOL && ps[j+1].op == IN_SCHOOL)
                {
                    //按入校时间排序
                    if(cmp(ps[j].time,ps[j+1].time) > 0)
                    {
                        t = ps[j];
                        ps[j] = ps[j+1];
                        ps[j+1] = t;
                    }
                }

            }
        }
    }
    printf("排序后：\n");
    show(ps,n);
}



