};

int main(void)
{
    int id = 0;
    struct mymesg ckxmsg;
    key_t key = ftok("/tmp",66);
    id = msgget(key,0666|IPC_CREAT);
    if(id == -1){
        printf("open msg error \n");
        return 0;
    }

    while(1){
        if(msgrcv(id,(void *)&ckxmsg,512,1,0) < 0){
