#define M 512
#define N 20

void tolowers(char *s)
{
    while (*s != '\0')
    {
        if (isupper(*s))
            *s = tolower(*s);
        s++;
    }
}

int tj(char *s, char *word, int *index)
{
    char w[N] = {'\0'};
    char word0[N] = {'\0'};

    char *p0 = strstr(s, word);

    if (p0 == NULL)
    {
        return 0;
    }

    index[0] = 0;
    index[1] = p0 - s;

    strncpy(word0, word, strlen(word));
    tolowers(word0);

    char *p = s;

    while (*p == ' ')
        p++;
    while (*p != '\0')
    {
        sscanf(p, "%s", w);
        tolowers(w);

        if (strcmp(w, word0) == 0)
            index[0] += 1;
        p += strlen(w);
        while (*p == ' ')
            p++;
    }

    return 1;
}

int main(int argc, char *argv[])
{
    char s[M], word[N];
