char *change(char *p)
{
	int i=0,j=0;
	char tem[2]={'\0'};
	for(j=0;j<strlen(p)/2;j++)
	{
		tem[0]=*(p+i);
		*(p+i)=*(p+i+1);
		*(p+i+1)=tem[0];
		i+=2;
	}
	printf("p==%s\n",p);
}

