int cmp(Date d1,Date d2)
{
    if(d1.y < d2.y)
        return -1;
    else if(d1.y > d2.y)
        return 1;
    else
    {
        if(d1.m < d2.m)
            return -1;
        else if(d1.m > d2.m)
            return 1;
        else
        {
            if(d1.d < d2.d)
