using namespace std;
using  DXS=unordered_map<int, int> ;
DXS operator+(const DXS& lh, const DXS rh) {
    DXS result;
    for (auto x : lh) {
        result[x.first] += x.second;
    }
    for (auto y : rh) {
        result[y.first] += y.second;
    }
    return result;
}
int main()
{
    vector<int> a = { -1, 17, 2, 23, 22, 2, 6, 8 ,-4, 7 ,-18, 0 ,1 ,5, 21, 4 ,0 ,-1 };
    vector<int> b = { 12 ,7 ,-7, 5, 3 ,17, 23, 4, 15, 10, -10, 5 ,13, 5, 2, 19, 9, -7 };
    DXS dxs1;
    DXS dxs2;
    DXS result;
    for (int i = 0; i < a.size() - 1; i += 2) {
        if (a[i] != 0 && a[i + 1] >= 0)
            dxs1[a[i+1]]+=a[i];
    }
    for (int i = 0; i < b.size() - 1; i += 2) {
        if (b[i] != 0 && b[i + 1] >= 0)
            dxs2[b[i + 1]] += b[i];
    }
    result = dxs1 + dxs2;
    vector<pair<int, int>> ans;
    for (auto x : result) {
        if (x.second != 0)
            ans.push_back(make_pair(x.second, x.first));
    }
    sort(ans.begin(), ans.end(), [](const pair<int,int>& lh,const pair<int,int>& rh) {
        return lh.second > rh.second;
        });
    for (auto x : ans) {
        cout << "[" << x.first << " " << x.second << "]";
    }
    return 0;

}

