void jia(char *str, char *key, int len)
{
    int i = 0;
    char ch;
    while (*str)
    {
        if (i == len)
            i = 0;
        ch = *str;
        ch += key[i];
        if (ch < ' ' || ch > 'z')
            ch %= 'z';
        *str = ch;
        str++;
        i++;
    }
}
void jie(char *str, char *key, int len)
{
    int i = 0;
    char ch;
    while (*str)
    {
        if (i == len)
            i = 0;
        ch = *str;
        ch -= key[i];
        if (ch < ' ' || ch > 'z')
            ch %= 'z';
        *str = ch;
        str++;
        i++;
    }
}

