template
class Polynomial
{
private:
	class Term
	{
	public:
		T xishu;
		int mi;
	};
	class Node
	{
	public:
		Term data;
		Node* next;
		Node(T x = 0, unsigned y = 0, Node* p = 0)
		{
			data.xishu = x;
			data.mi = y;
			next = p;//ä¿®æ”¹
		}
	};
public:
	Polynomial();
	void myfree();//é‡Šæ”¾é“¾è¡¨æ‰€æœ‰å„èŠ‚ç‚¹
	Polynomial& operator=(const Polynomial&);
	Polynomial operator+(const Polynomial&);
	Polynomial operator*(const Polynomial&);
	void initial();
