template
Polynomial::Polynomial()
{
	first = new Node;
	num = 0;
	highestdigit = 0;
}

template
void Polynomial::myfree()    //é‡Šæ”¾é“¾è¡¨æ‰€æœ‰èŠ‚ç‚¹
{
	Node* p = first, *q;
	while (p)
	{
		q = p->next;
		delete p;
		p = q;
	}
	first=NULL;//ä¿®æ”¹
	num = 0; highestdigit = 0;//ä¿®æ”¹
}

template
Polynomial& Polynomial::operator=(const Polynomial& second)
{
	myfree();
	highestdigit = second.highestdigit;
	num = second.num;
	first = second.first;

	return *this;
}

template
Polynomial Polynomial::operator+(const Polynomial& second)
{
	Polynomial sum;
	Node* pa = first->next, *pb = second.first->next, *pc = sum.first;
	while (pa&&pb)
	{
		if (pa->data.mi == pb->data.mi)  // æŒ‡æ•°ç›¸ç­‰æ—¶  
		{
			T x = pa->data.xishu + pb->data.xishu;
			if (x)     // ç›¸åŠ å®Œçš„ç³»æ•°ä¸ä¸º0æ—¶  
			{
				pc->next = new Node(x, pa->data.mi);
				pc = pc->next;
				++sum.num;
				if(sum.highestdigitdata.mi) sum.highestdigit = pa->data.mi;
			}
			pa = pa->next;
			pb = pb->next;
		}
		else if (pa->data.mi > pb->data.mi){
			pc->next = new Node(pa->data.xishu, pa->data.mi);
			if(sum.highestdigitdata.mi) sum.highestdigit = pa->data.mi;
			++sum.num;
			pc = pc->next;
			pa = pa->next;
		}
		else{
			pc->next = new Node(pb->data.xishu, pb->data.mi);
			++sum.num;
			if(sum.highestdigitdata.mi) sum.highestdigit = pb->data.mi;
			pc = pc->next;
			pb = pb->next;
		}
	}
	while (pa){
		pc->next = new Node(pa->data.xishu, pa->data.mi);
		++sum.num;
		if(sum.highestdigitdata.mi) sum.highestdigit = pa->data.mi;
		pc = pc->next;
		pa = pa->next;
	}
	while (pb){
		pc->next = new Node(pb->data.xishu, pb->data.mi);
		++sum.num;
		if(sum.highestdigitdata.mi) sum.highestdigit = pb->data.mi;
		pc = pc->next;
		pb = pb->next;
	}
	return sum;
}
template
Polynomial Polynomial::operator*(const Polynomial& second){
	Polynomial multi, temp;
	Node* pa = first->next, *pb = second.first->next, * pt;

	if (num >= second.num){ //æ‹¿é¡¹æ•°å¤šçš„é‚£ä¸ªé“¾è¡¨ä¹˜ä»¥é¡¹æ•°å°‘çš„é‚£ä¸ª
		while(pb){          //pbæ˜¯é¡¹æ•°å°‘çš„é‚£ä¸ªï¼Œ
			pt = temp.first;
			while (pa){     //é¡¹æ•°å¤šçš„å¼€å§‹éå†  
				pt->next = new Node(pa->data.xishu*pb->data.xishu, pa->data.mi+pb->data.mi);//ä¿®æ”¹
				temp.num++;//ä¿®æ”¹
				if( temp.highestdigitnext->data.mi ) temp.highestdigit=pt->next->data.mi;//ä¿®æ”¹
				pt = pt->next;
				pa = pa->next;
			}
