//双边沿触发器
module dual_edge_module
(
    input    clk,
    input    d,
    output    q
);
reg    q1,q2;
always @(posedge clk)
begin
    q1 <= d;
end

always @(negedge clk)
begin
    q2 <= d;
end
assign q = clk ? q1:q2;
endmodule

