unsigned char code table1[]={0x3f,0x06,0x5b,0x4f,0x66,0x6d,0x7d,0x07,0x7f,0x6f};
unsigned char code table2[]={0x7F,0xBF,0xDF,0xEF,0xF7,0xFB,0xFD,0XFE};
unsigned char code xuehao[]={0x66,0x5b,0x3f,0x3f,0x66,0x4f,0x3f,0x7d};

sbit DU=P0;
sbit WE=P2;
sbit k1=P3^4;
sbit k2=P3^5;
sbit k3=P3^2;
sbit DA=P1^7;
sbit MA=P1;

void delay(unsigned int t)
{       
    unsigned int j=0;
    for(;t>0;t--)
        for(j=0;j<125;j++);
}

void timer0()interrupt 1//定时中断1
{     
    DA=!DA;
    TH0=0Xfe;
    TL0=0X0c;
}

void timer1()interrupt 3//定时中断2
{        
    TH0=0XFC;
    TL0=0X18;
    if(k1==0)
    {
     delay(10);
        if(k1==0)
        {
            while(1)
            {
             int i;
                i++;
                MA=table1[i];
            }
        }
    }
}

wbzd1() interrupt 0//外部中断1
{
if(k1==0)
    {
        delay(10);
        if(k1==0)
        {
       TMOD=0X01;        //执行定时中断1   
       TH0=0Xfe;
       TL0=0X0c;
       ET0=1;
       TR0=1;
       do{}while(1);
        }
    }
}

if(k2==0)
{
    delay(10);
    if(k2==0)
    {
      TMOD=0X01;             //执行定时中断2
      TH1=0XFC;
      TL1=0X18; 
      ET1=1;
      TR1=1;
    }
}

wbzd2() interrupt 2//外部中断2
{
      EA=0;
}

 void int0()//执行外部中断1
{
  IT0=1;
  EA=1;
  EX0=1;
 }

void int1()//执行外部中断2
{ 
  IT1=1;
  EA=1;
  EX1=1;
 }


void main()
{
     while(1)
         {
      int i;
      for(i=0;i<=8;i++)
             {
       P0=xuehao[i];
       P2=table2[i];
       delay(500);
       if(k1==0|k2==0)
             {
        int0();
                }
        if(k3==0)
            {
        int1();
      }
    }
  }
}

