public class MatrixEquation

    {

        private double[,] gaussMatrix;

        private int coe;



    public MatrixEquation()
    {
    }

    public MatrixEquation(double[] arrX, double[] arrY, int n)
    {
        coe = n;
        gaussMatrix = GetGauss(GetXPowSum(arrX, n), GetXPowYSum(arrX, arrY, n), n);
    }

    public double[,] GetGaussMatrix()
    {
        return gaussMatrix;
    }

    public double[] GetResult()
    {
        return ComputeGauss(gaussMatrix, coe);
    }

    ///Â Â è®¡ç®—è·å–xæ•£ç‚¹çš„å¹‚æ¬¡å’Œæ•°ç»„Â Â 
    ///Â xæ•£ç‚¹åºåˆ—Â 
    ///Â å‡½æ•°æ‹Ÿåˆæ¬¡æ•°Â 
    ///Â Â 
    protected double[] GetXPowSum(double[] arrX, int n)
    {
        int m = arrX.Length;//Xæ•£ç‚¹çš„ä¸ªæ•°

        double[] xPow = new double[2 * n + 1]; //å­˜å‚¨Xæ•£ç‚¹çš„å¹‚æ¬¡å€¼

        for (int i = 0; i < xPow.Length; i++)
        {
            if (i == 0)
            {
                xPow[i] = m;
            }
            else
            {
                //è®¡ç®—xçš„iæ¬¡æ–¹å’Œ

                double max = 0;
                for (int j = 0; j < m; j++)
                {
                    if (arrX[j] == 0)
                        max = max + 1;
                    else
                        max = max + Math.Pow(arrX[j], i);
                }
                xPow[i] = Math.Round(max, 4);
            }
        }
        return xPow;
    }

    ///Â è®¡ç®—è·å–xyçš„å¹‚æ¬¡å’Œåºåˆ— Â 
    ///Â xæ•£ç‚¹åºåˆ—Â 
    ///Â yæ•£ç‚¹åºåˆ—Â 
    ///Â æ‹Ÿåˆæ›²çº¿æ¬¡æ•°Â 
    ///Â Â 
    protected double[] GetXPowYSum(double[] arrX, double[] arrY, int n)
    {
        int m = arrX.Length;//Xæ•£ç‚¹çš„ä¸ªæ•°

        double[] xyPow = new double[n + 1]; //ä»“å‚¨Xæ•£ç‚¹çš„å¹‚æ¬¡å€¼

        for (int i = 0; i < xyPow.Length; i++)
        {
            //è®¡ç®—xyçš„iæ¬¡æ–¹å’Œ

            double max = 0;
            for (int j = 0; j < m; j++)
            {
                if (arrX[j] == 0)
                    max = max + 1;
                else
                    max = max + Math.Pow(arrX[j], i) * arrY[j];
            }
            xyPow[i] = Math.Round(max, 4);
        }
        return xyPow;
    }

    ///Â Â è·å–é«˜æ–¯çŸ©é˜µ(å¢å¹¿çŸ©é˜µ)Â         Â Â Â Â 
    ///Â Xçš„å¹‚æ¬¡å’ŒÂ 
    ///Â XYçš„å¹‚æ¬¡å’ŒÂ 
    ///Â æ‹Ÿåˆæ›²çº¿æ¬¡æ•°Â 
    ///Â Â 
    protected double[,] GetGauss(double[] arrX, double[] arrXY, int n)
    {
        double[,] gauss = new double[n + 1, n + 2];
        for (int i = 0; i < n + 1; i++)
        {
            int j;
            int m = i;
            for (j = 0; j < n + 1; j++)
            {
                gauss[i, j] = arrX[m];
                m++;
            }
            gauss[i, j] = arrXY[i];
        }

        return gauss;
    }

    ///Â Â æ±‚è§£æ‹Ÿåˆæ›²çº¿çš„ç³»æ•°Â Â Â         Â Â Â Â Â Â Â Â 
    ///Â çº¿æ€§æ–¹ç¨‹çš„å¢å¹¿çŸ©é˜µÂ 
    ///Â æ–¹ç¨‹æ¬¡æ•°Â 
    ///Â Â 
    protected double[] ComputeGauss(double[,] gauss, int n)
    {
        double[] a = new double[n + 1];
        double s;
        int matrixLine = n + 1;
        for (int i = 0; i < n + 1; i++)
            a[i] = 0;
        //å¾ªç¯æ¯åˆ—

        for (int j = 0; j < matrixLine; j++)
        {
            //æ¯åˆ—Jè¡Œä»¥åçš„ç»å¯¹å€¼æœ€å¤§å€¼

            double max = 0;
            int k = j;
            for (int i = j; i < matrixLine; i++)
            {
                if (Math.Abs(gauss[i, j]) > max)
                {
                    max = gauss[i, j];
                    k = i;
                }
            }
            //åˆ¤æ–­jè¡Œå¦ä¸ºæœ€å¤§å€¼è¡ŒÂ è‹¥ä¸æ˜¯å°†jè¡Œè°ƒæ¢ä¸ºæœ€å¤§å€¼è¡Œ

            if (k != j)
            {
                double temp; for (int m = j; m < matrixLine + 1; m++)
                {
                    temp = gauss[j, m];
                    gauss[j, m] = gauss[k, m];
                    gauss[k, m] = temp;
                }
            }
            if (max == 0)
            {
                //å¥‡å¼‚çŸ©é˜µæ— è§£

                return a;
            }
            //è¿›è¡Œåˆç­‰è¡Œå˜æ¢å¾—åˆ°ä¸Šä¸‰è§’çŸ©é˜µ

            for (int i = j + 1; i < matrixLine; i++)
            {
                s = gauss[i, j];
                for (int m = j; m < matrixLine + 1; m++)
                {
                    gauss[i, m] = Math.Round(gauss[i, m] - gauss[j, m] * s / gauss[j, j], 6);
                }
            }
        }
        //æ ¹æ®å€’æ¨æ–¹å¼ä¸€æ¬¡è®¡ç®—ç°è¡Œæ–¹ç¨‹çš„è§£

        for (int i = matrixLine - 1; i >= 0; i--)
        {
            s = 0;
            for (int j = i + 1; j < matrixLine; j++)
            {
                s += gauss[i, j] * a[j];
            }
            a[i] = Math.Round((gauss[i, matrixLine] - s) / gauss[i, i], 6);
        }
        //è¿”å›æ–¹ç¨‹çš„è§£å³æ‹Ÿåˆæ›²çº¿çš„ç³»æ•°

        return a;
    }
}
