% æ˜¾ç¤ºæ¬§æ‹‰
function [T,X,dX] = ODE_ExplicitEuler( Hfun,t,h,x0 )
    % ç¡®å®šæ—¶é—´èŠ‚ç‚¹
    T = t(1):h:t(2);
    % è®¡ç®—
    N = length(T);
    x0 = x0(:);  x0 = x0';     % åˆå€¼å˜ä¸ºè¡Œå‘é‡  
    m = length(x0);            % çŠ¶æ€é‡ç»´æ•°
    X = zeros(N,m);            % åˆå§‹åŒ–çŠ¶æ€é‡
    dX = zeros(N,m);           % çŠ¶æ€å¯¼æ•°
    X(1,:) = x0;
    for k = 2:N
        dX(k-1,:) = Hfun( T(k-1),X(k-1,:) );   
        h = T(k) - T(k-1);
        X(k,:) = X(k-1,:) + h*dX(k-1,:);                        
    end
    dX(N,:) = Hfun( T(N),X(N,:) );
end
% éšå¼æ¬§æ‹‰
function [T,X,dX] = ODE_ImplicitEuler( Hfun,t,h,x0 )
    T = t(1):h:t(2);
    % è®¡ç®—
    N = length(T);
    x0 = x0(:);  x0 = x0';     % åˆå€¼å˜ä¸ºè¡Œå‘é‡  
    m = length(x0);            % çŠ¶æ€é‡ç»´æ•°
    X = zeros(N,m);            % åˆå§‹åŒ–çŠ¶æ€é‡
    dX = zeros(N,m);           % çŠ¶æ€å¯¼æ•°
    X(1,:) = x0;
    for k = 2:N
        h = T(k) - T(k-1);
        for jj = 1:m
           Ind =  zeros(1,m);         % çŠ¶æ€é€‰æ‹©å‘é‡
           Ind(jj) = 1;        % é€‰æ‹©çš„å‘é‡ä½ç½®  
           fh = @(x) h*Ind*Hfun(T(k),[X(k-1,1:jj-1)';x; X(k-1,jj+1:m)']) + X(k-1,jj)' - x;    
           X(k,jj) = fzero( fh,X(k-1,jj) ) ;   
        end                  
    end
    dX(N,:) = Hfun( T(N),X(N,:) );
end

function [T,X,dX] = ODE_2OrderEuler( Hfun,t,h,x0 )
    T = t(1):h:t(2);
    % è®¡ç®—
    N = length(T);
    x0 = x0(:);  x0 = x0';     % åˆå€¼å˜ä¸ºè¡Œå‘é‡  
    m = length(x0);            % çŠ¶æ€é‡ç»´æ•°
    X = zeros(N,m);            % åˆå§‹åŒ–çŠ¶æ€é‡
    dX = zeros(N,m);           % çŠ¶æ€å¯¼æ•°
    X(1,:) = x0;
    for k = 2:N
        dX(k-1,:) = Hfun( T(k-1),X(k-1,:) );   
        h = T(k) - T(k-1);
        if k == 2
            X(k,:) = X(k-1,:) + h*dX(k-1,:);    
        else
            X(k,:) = X(k-2,:) + 2*h*dX(k-1,:);   
        end
    end
    dX(N,:) = Hfun( T(N),X(N,:) );
end
function [T,X,dX] = ODE_ImprovedEuler( Hfun,t,h,x0 )
    T = t(1):h:t(2);
    % è®¡ç®—
    N = length(T);
    x0 = x0(:);  x0 = x0';     % åˆå€¼å˜ä¸ºè¡Œå‘é‡  
    m = length(x0);            % çŠ¶æ€é‡ç»´æ•°
    X = zeros(N,m);            % åˆå§‹åŒ–çŠ¶æ€é‡
    dX = zeros(N,m);           % çŠ¶æ€å¯¼æ•°
    X(1,:) = x0;
    for k = 2:N
        dX(k-1,:) = Hfun( T(k-1),X(k-1,:) );   
        h = T(k) - T(k-1);
        Xp = X(k-1,:) + h*dX(k-1,:);
        dXp = Hfun( T(k),Xp );
        X(k,:) = X(k-1,:) + (h/2)*(dX(k-1,:)+dXp');
    end
    dX(N,:) = Hfun( T(N),X(N,:) );
end

