void swap(int* u, int* v);

void swap(int* u, int* v)
{
    int temp;
    temp = *u;
    *u = *v;
    *v = temp;
}
void bubbleSort(int* a, int p, int r)
{
    int i, j;
    for (i = p; i < r; i++)
    {
        for (j = i + 1; j <= r; j++)
        {
            if (a[j] < a[i]) swap(&a[i], &a[j]);
        }
    }
}

int Partition(int* a, int p, int r, int val)
{
    int pos;
    int q, i, j, x;
    for (q = p; q <= r; q++)
    {
        if (a[q] == val)
        {
            pos = q;
            break;
        }
    }
    swap(&a[p], &a[pos]);

    i = p; j = r + 1; x = a[p];
    while (1)
    {
        while (a[++i] < x && i < r);
        while (a[--j] > x);
        if (i >= j) break;
        swap(&a[i], &a[j]);
    }
    a[p] = a[j]; a[j] = x;
    return j;
}

int Select(int* a, int p, int r, int k)
{
    int s, t;
    int i, j, n, x;
    if (r - p < 75)
    {
        bubbleSort(a, p, r);
        return a[p + k - 1];
    }
    //æ‰¾ä¸­ä½æ•°çš„ä¸­ä½æ•°ï¼Œr-p-4å³ä¸Šé¢æ‰€è¯´çš„n-5
    for (i = 0; i <= (r - p - 4) / 5; i++) //æŠŠæ¯ä¸ªç»„çš„ä¸­ä½æ•°äº¤æ¢åˆ°åŒºé—´[p,p+(r-p-4)/4]
    {
        s = p + 5 * i; t = s + 4;
        for (j = 0; j < 3; j++) //å†’æ³¡æ’åºï¼Œä»åå¼€å§‹æ’ï¼Œç»“æœä½¿å¾—åä¸‰ä¸ªæ•°æ˜¯æ’å¥½é¡ºåºçš„ï¼ˆé€’å¢ï¼‰
        {
            for (n = s; n < t - j; n++)
            {
                if (a[n] > a[n + 1]) swap(&a[n], &a[n + 1]);
            }
        }
        swap(&a[p + i], &a[s + 2]);//äº¤æ¢æ¯ç»„ä¸­çš„ä¸­ä½æ•°åˆ°å‰é¢
    }
    //(r-p-4)/5è¡¨ç¤ºç»„æ•°-1ï¼Œåˆ™[p,p+(r-p-4)/5]çš„åŒºé—´é•¿åº¦ç­‰äºç»„æ•°
    x = Select(a, p, p + (r - p - 4) / 5, (r - p + 1) / 10);//æ±‚ä¸­ä½æ•°çš„ä¸­ä½æ•°
    /*
    (r-p+1)/10 = (p+(r+p-4)/5-p+1)/2
