#define _CRT_SECURE_NO_WARNINGS 1
#include<stdio.h>
#include<stdlib.h>
#include<assert.h>
typedef int datatype;
typedef struct node {
    datatype data;
    struct node* link;
}node;

node* BuySLTNode(datatype x)
{
    node* newnode = (node*)malloc(sizeof(node));
    if (newnode == NULL)
    {
        perror("malloc fail");
        exit(-1);
    }
    newnode->data = x;
    newnode->link = NULL;
    return newnode;
}

node* CreateSList()
{
    datatype data;
    node* phead = NULL, * ptail = NULL;
    while (1)
    {
        scanf("%d", &data); //ä¿®æ”¹  
        node* newnode = BuySLTNode(data);
        if (phead == NULL)
            phead = newnode;//ä¿®æ”¹ 
        else
            ptail->link = newnode;
        ptail = newnode;   //ä¿®æ”¹ 
        if ((getchar()) == '\n')  break; //ä¿®æ”¹
    } 
    return phead;
}

node* Find_max(node* phead)   
{
    node* cur = phead;
    node* Max = phead;
    datatype max = cur->data;
    while (cur)
    {
        if (cur->data > max)
        {
            max = cur->data;
            Max = cur;
        }
        cur = cur->link;
    }
    return Max;
}

void SListErase(node** pphead, node* pos) 
{
    assert(pos);
    assert(*pphead);
    if (pos == *pphead)
    {
        node* next = (*pphead)->link;
        free(*pphead);
        *pphead = next;
    }
    else
    {
        node* prev = *pphead;
        while (prev->link != pos)
        {
            prev = prev->link;
        }
        prev->link = pos->link;
        free(pos);
    }
}
void SLTPushBack(node** pphead, datatype x) 
{
    node* newnode = BuySLTNode(x);
    if (*pphead == NULL)
    {
        *pphead = newnode;
    }
    else
    {
        node* tail = *pphead;
        //æ‰¾å°¾
        while (tail->link)
        {
            tail = tail->link;
        }
        tail->link = newnode;
    }
}

void SLTPrint(node* phead)
{
    node* cur = phead;
    while (cur)
    {
        printf("%d ", cur->data); //ä¿®æ”¹
        cur = cur->link;
    }
}

int main()
{
    node* plist = NULL;   //(node*)malloc(sizeof(node)); ä¿®æ”¹
    plist = CreateSList();
    node* max = Find_max(plist);   
    SLTPushBack(&plist, max->data); //ä¿®æ”¹ SLTPushBack(plist, max->data);
    SListErase(&plist, max);        //ä¿®æ”¹ SListErase(plist, max);
    SLTPrint(plist);
    return 0;
}

