Node* reverselink(struct Node* node, int K)
{
    struct Node* initnode = node;
    struct Node* newnode = node -> link;
    struct Node* oldnode = newnode -> link;
    struct Node* temp;

    int count = 1;

    while(count < K)
    {
        temp = oldnode -> link;
        oldnode -> link = newnode;
        newnode = oldnode;
        oldnode = temp;
        count++;
    }

    initnode -> link -> link = oldnode;

    return newnode;
}

