using namespace std;

void swap(int *a, int *b) {
  int t = *a;
  *a = *b;
  *b = t;
}

void selectionsort(int a[], int n) {
  for (int i = 0; i < n - 1; i++) {
    int min_idx = i;
    for (int j = i + 1; j < n; j++)
      if (a[j] < a[min_idx])
        min_idx = j;
    if (min_idx != i)
      swap(&a[min_idx], &a[i]);
  }
}

int binarysearch(int a[], int n, int value) {
  int left = 0, right = n - 1;
  while (left <= right) {
    int m = (left + right) / 2;
    if (a[m] < value)
      left = m + 1;
    else if (a[m] > value)
      right = m - 1;
    else
      return m;
  }
  return -1;
}

