#include <stdio.h>

void row_reduce(double A[][3], double b[], int n) {
    int i, j, k;
    for (i = 0; i < n; i++) {
        double pivot = A[i][i];
        for (j = i; j < n; j++) {
            A[i][j] /= pivot;
        }
        b[i] /= pivot;
        for (k = i+1; k < n; k++) {
            double factor = A[k][i] / A[i][i];
            for (j = i+1; j < n; j++) {
                A[k][j] -= factor * A[i][j];
            }
            b[k] -= factor * b[i];
        }
    }
}

void back_substitute(double A[][3], double b[], double x[], int n) {
    int i, j;
    for (i = n-1; i >= 0; i--) {
        double sum = 0.0;
        for (j = i+1; j < n; j++) {
            sum += A[i][j] * x[j];
        }
        x[i] = b[i] - sum;
    }
}

int main() {
    double A[3][3] = {{3, 2, 1}, {1, -1, 2}, {2, 3, -1}};
    double b[3] = {10, -1, 7};
    double x[3];
    row_reduce(A, b, 3);
    back_substitute(A, b, x, 3);
    printf("Solution: (%f, %f, %f)\n", x[0], x[1], x[2]);
    return 0;
}



