int Binsearch(long id[],long m,int n)

    {

        int high = n-1;

        int low = 0;

        int mid;



    while(low <= high)
    {
        mid = (high+low)/2;
        if(m < id[mid])
        {
            high = mid-1;
        }
        else if(m > id[mid])
        {
            low = mid+1;
        }
        else
        {
            return mid;
        }
    }
    return -1;
}




