        public  boolean downloadFile(String pathname, String filename, String localpath){ 
            boolean flag = false; 
            OutputStream os=null;
            try { 
                System.out.println("å¼€å§‹ä¸‹è½½æ–‡ä»¶");
                initFtpClient();
