#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

int main()
{
    vector<int> vec;
    int t;
    //输入整数，直到输入0为止
    while (1)
    {
        cin >> t;
        if (t != 0)
            vec.push_back(t);
        else
            break;
    }
    //排序
    sort(vec.begin(), vec.end());
    //输出
    //1.第一种方式，使用成员函数
    for (int i = 0; i < vec.size(); i++)
    {
        if (i < vec.size() - 1)
            cout << vec.at(i) << " ";
        else
            cout << vec.at(i) << endl;
    }
    //2.第二种方式，使用迭代器
    vector<int>::iterator it = vec.begin();
    for (; it != vec.end(); it++)
    {
        if (it == vec.begin())
            cout << *it;
        else
            cout << " " << *it;

    }
    cout << endl;
    //3.第三种方式，使用for_each
    for_each(vec.begin(), vec.end(), [](int elem) {std::cout << elem << " "; }
    );
    return 0;
}

