float arr_add(float a[], int n)
{
    int i;
    float sum = 0;
    for (i = 0; i < n; i++)
        sum += a[i];
    return sum;
}
float add_add(float* p, int a)
{
    int i = 0;
    float sum = 0;
    while (i < a) {
        sum += *(p + i);
        i++;
    }
    return sum;
}
float process(float* p, int n, float (*f)(float*, int))
{
    return (*f)(p, n);
}

