    {
        //取得下一文件名
        if (0 == DragQueryFile(hdrop, uFile, szFile, MAX_PATH))
        {
            continue;
        }

        //跳过文件夹，也可以处理文件夹
        if (PathIsDirectory(szFile))
        {
            continue;
        }

        //添加文件名到我们的文件名列表中。
        m_lsFiles.push_back(szFile);
    }

    //释放资源
    GlobalUnlock(stg.hGlobal);
    ReleaseStgMedium(&stg);

    //检查有几个文件被选中，如果大于属性表能有的最大属性页的个数，删减列表
    if (m_lsFiles.size() > MAXPROPPAGES)
    {
        m_lsFiles.resize(MAXPROPPAGES);
    }

    //如果我们发现可以操作的任一文件返回S_OK，否则返回E_FAIL
    return (m_lsFiles.size() > 0) ? S_OK : E_FAIL;
}


HRESULT CFileTimeShlExt::AddPages(LPFNADDPROPSHEETPAGE lpfnAddPageProc, LPARAM lParam)
{
    PROPSHEETPAGE psp;
    TCHAR szPageTitle[MAX_PATH];
    string_list::const_iterator it, itEnd;

    for (it = m_lsFiles.begin(), itEnd = m_lsFiles.end(); it != itEnd; it++)
    {
        //it 指向下一个文件名，分配一个给页面使用弄个的字符串拷贝
        LPCTSTR szFile = _tcsdup(it->c_str());

        //从文件名中截去路径和扩张名，用其作为页面标题，该名称截取为24个字符以适合Tab的大小
        lstrcpy(szPageTitle, it->c_str());
        PathStripPath(szPageTitle);
        PathRemoveExtension(szPageTitle);
        szPageTitle[24] = '\0';

        //由于直接使用SDK调用完成属性页，我们得亲自处理PROPSHEETPAGE结构
        psp.dwSize = sizeof(PROPSHEETPAGE);
        psp.dwFlags = PSP_USEREFPARENT | PSP_USETITLE | PSP_DEFAULT | PSP_USEHICON | PSP_USECALLBACK;
        psp.hInstance = _pModule->GetModuleInstance();
        psp.pszTemplate = MAKEINTRESOURCE(IDD_PROPPAGE);
        psp.pszIcon = MAKEINTRESOURCE(IDC_ICON);
        psp.pszTitle = szPageTitle;
        psp.pfnDlgProc = (DLGPROC)ProPageDlgProc;
        psp.lParam = (LPARAM)szFile;
        psp.pfnCallback = PropPageCallbackProc;
        psp.pcRefParent = (UINT *)_pModule->GetModuleInstance();

        HPROPSHEETPAGE hPage = CreatePropertySheetPage(&psp);
        if (NULL != hPage)
        {
            //调用Shell的回调函数添加新创建的属性页到属性对话框中
            if (!lpfnAddPageProc(hPage, lParam))
            {
                DestroyPropertySheetPage(hPage);
            }
        }
    }
    return S_OK;
}

BOOL CALLBACK ProPageDlgProc(HWND hwnd, UINT uMsg, WPARAM wParam, LPARAM lParam)
{
    switch (uMsg)
    {
    case WM_COMMAND:
        switch (LOWORD(wParam))
        {
        case IDC_BUTTON:
            MessageBox(NULL, L"您点击了第一个按钮。", L"提示", MB_OK);
            break;
        }
        break;
    }
    return TRUE;
}

UINT CALLBACK PropPageCallbackProc(HWND hwnd, UINT uMsg, LPPROPSHEETPAGE ppsp)
{
    if (PSPCB_RELEASE == uMsg)
    {
        free((void *)ppsp->lParam);
    }
    return 1;
}

java对象属性的默认值影响sql查询语句的拼接，该如何处理。
1、问题描述：

想做一个通用的sql查询语句，通过传入对象的属性赋值情况，拼接查询语句。比如Admin类，有int id,String name,int age,boolean isLeader 4个属性，如果给Admin的对象的某几个属性赋值，比如：setAge(32);setName（“张”）。那么语句拼接为select * from admin where age=32 and name like '%张%'。我在拼接时本来想通过属性是否为null，或者是否为空字符来判断是否作为查询条件，但是由于默认值的存在，导致拼接错误。

2、代码



/***
 * 约定：
 * 1、类名与表名对应，表名全小写。
 * 2、类属性与列名一致。
 * 3、ID列的类属性和列名为=表名（第一个字母小写）+“Id” * 
 * 4、暂不支持联表查询。
 *类名：BaseDao
 * ***/
public  List query(T t) throws IllegalArgumentException, IllegalAccessException, SQLException{
        StringBuilder sql=new StringBuilder();
        StringBuilder whereString=new StringBuilder();
        List parameters=new ArrayList();
        Class clazz=t.getClass();
        //获取类名
        String classSimpleName=clazz.getSimpleName();
        //获取表名
        String tableName=classSimpleName.toLowerCase();
        //拼接select语句前半部分
        sql.append("select * from "+tableName);
        //获取所有类属性
        Field[] fields=clazz.getDeclaredFields();
        //遍历所有属性
        for(Field field:fields) {           
            field.setAccessible(true);
            //得到属性值
            Object fieldValue=field.get(t);

            if(fieldValue!=null) {              
            //如果属性值不为null，获取属性名（字段名）。
                String fieldName=field.getName();
                //判断是否为String类型
                if(fieldValue instanceof String) {
            //  如果是String,判断是否为空字符
                    if(!("".equals(((String)fieldValue).trim()))) {
                    //不是空字符串，作为where条件，并将参数加入列表。
                        whereString.append(fieldName+" like ? and ");
                        parameters.add("%"+(String)fieldValue+"%");
                    }                       
                }else if("Integer".equals(fieldValue.getClass().getSimpleName())){
                /***判断是否为int，如果int>0，认为参数有效---这个也是为了处理默认值为0的情况，想的办法，但是有时候表里面字段值可能<=0，所以我觉得处理得不**好*/
                    if((int)fieldValue>0) {
                        whereString.append(fieldName+"=? and ");
                        parameters.add(fieldValue);
                    }
                }else{
                /*其他情况直接作为where查询条件，但是运行发现boolean值默认false，也会直接当成是有效条件，影响条件设置*/
                    whereString.append(fieldName+"=? and ");
                    parameters.add(fieldValue);
                }
            }
        }
        //判断是否有有效条件，有的话拼接where语句。
        if(parameters.size()>0) {
            whereString.delete(whereString.length()-5, whereString.length()-1);
            sql.append(" where ");
            sql.append(whereString);
        }   
        System.out.println(sql.toString());
        System.out.println(parameters);
        //连接数据库，查询。
        QueryRunner qr=JdbcPool.getQueryRunner();
//      return null;
        return qr.query(sql.toString(), new BeanListHandler(clazz), parameters.toArray());
    }





