/**

     * 获取按年数分类，通用

     * @return

     */

    private Field getYear() {

        Field useTime = DSL.field("YEAR(now()) - YEAR(TBL_ELEVATOR.service_date)");

        Field year = DSL.decode()

                .when(useTime.between(0, 4), "5年以内")

                .when(useTime.between(5, 9), "5年至10年")

                .when(useTime.between(10, 14), "10年至15年")

                .otherwise("15年以上")

                .as("name");

        return year;

    }



/**
 * 按投入使用年限分的电梯总数
 * @param condition
 * @return
 */
private Table getYearsTotalCountTable(Map condition) {
    Field year = getYear();

    SelectJoinStep record = dsl.select(DSL.count().as("yearsCount"))
            .select(year)
            .from(TBL_ELEVATOR)
            .leftJoin(TBL_USER_DEV)
            .on(TBL_ELEVATOR.USER_DEV_ID.eq(TBL_USER_DEV.ID));

    return record.groupBy(year)
            .asTable("totalCount");
}

/**
 * 按投入使用年限分：获取故障数量
 * @param condition
 * @return
 */
private Table getYearsDisplayCountTable(Map condition) {
    Field year = getYear();

    SelectJoinStep record = dsl.select(
            DSL.count().as("malfunctionCount")
            )
            .select(year)
            .from(TBL_WARNING)
            .leftJoin(TBL_USER_DEV)
            .on(TBL_WARNING.USER_DEV_ID.eq(TBL_USER_DEV.ID))
            .leftJoin(TBL_ELEVATOR)
            .on(TBL_ELEVATOR.USER_DEV_ID.eq(TBL_USER_DEV.ID));
    List conditions = getTypeConditions(condition);

    return record.where(DSL.and(conditions))
            //.and(TBL_WARNING.STATUS.eq(CommonCodeConstants.WARNING_STATUS_CONFIRM))
            .groupBy(year)
            .asTable("dispalyCount");

}

/**
 * 投入使用年限
 * @param condition
 * @param page
 * @return
 */
@SuppressWarnings("unchecked")
public List getUseYearsStatistics(Map condition, Pagination page) {

    Table totalCount = getYearsTotalCountTable(condition);
    Field years = (Field) totalCount.field("name");
    Field yearsCount = (Field) totalCount.field("yearsCount");

    Table dispalyCount = getYearsDisplayCountTable(condition);
    Field dispalyYears = (Field) dispalyCount.field("name");
    Field dispalyYearsCount = (Field) dispalyCount.field("malfunctionCount");



    SelectJoinStep reacord = dsl
            .select(
                    DSL.decode()
                    .when(dispalyYearsCount.isNull(), 0)
                    .otherwise(dispalyYearsCount)
                    .as("malfunctionCount")
                    )
            .select(
                    DSL.decode()
                    .when(yearsCount.isNull(), 0)
                    .otherwise(yearsCount)
                    .as("eleCount")
                    )
            .select(years)
            .from(totalCount)
            .leftJoin(dispalyCount)
            .on(years.eq(dispalyYears));


    return reacord.groupBy(years,dispalyYearsCount,yearsCount)
            .fetch()
            .into(MalfunctionStatistics.class);
}




