def change(data, sortType):
    test = []
    for item in data:
        result = []
        for value in item:
            l = list(set(item))
            l.sort(reverse=sortType)
            for index1, value1 in enumerate(l, 1):
                if value1 == value:
                    result.append(index1)
        test.append(result)
    return test


