struct Data
{
    char num[20];//编号
    char name[20];//姓名
    float playerScore[10];//十个评委的成绩 
    float sumScore;//总成绩
    float aveScore;//平均成绩 
}data;

void getContext()// 输入选手数据
{
    printf("请输入选手数据：(编号 姓名 )\n");
    printf("按999结束\n");
    getchar();
    while(1)
    {
        gets(data.num);
        if(strcmp(data.num,"999") == 0)//相等返回0 
