#include <stdio.h>
float process(float* p, int n, float (*f)(float*,int));
float arr_add(float a[], int n);
float add_add(float *p,  int a);
int main()
{
    int   n = 5;
    float a[5] = { 1,2,3,4,5 };
    printf("sum(arr_add)=%f\n", process(a, n, arr_add));
    printf("sum(add_add)=%f\n", process(a, n, add_add));
}
float arr_add(float a[], int n)
{
    int i;
    float sum = 0;
    for (i = 0; i < n; i++)
        sum += a[i];
    return sum;
}
float add_add(float* p, int a)
{
    int i = 0;
    float sum = 0;
    while (i < a) {
        sum += *(p + i);
        i++;
    }
    return sum;
}
float process(float* p, int n, float (*f)(float*, int))
{
    return (*f)(p, n);
}

