				post.setHeader("Content-type", "application/viid+json; charset=utf-8");
				if (headValue != null)
					post.setHeader("User-Identify", headValue);
				post.setEntity(new StringEntity(jsonParam, "UTF-8"));// å‘é€xmlå­—ç¬¦ä¸²æ•°æ®
				HttpResponse response = httpClient.execute(post);
				String result = EntityUtils.toString(response.getEntity());
				System.out.println("è¿”å›çš„æ¶ˆæ¯ï¼š" + result);
				System.out.println("è¿”å›çš„çŠ¶æ€: " + response.getStatusLine().getStatusCode());
