				sc = response.getStatusLine().getStatusCode();
				EntityUtils.consume(response.getEntity());
				
				httpClient.close();
				
				
			} catch (Exception e) {
				e.printStackTrace();
			} finally {
				if (httpClient != null) {
					try {
						httpClient.close();
					} catch (IOException e) {
						e.printStackTrace();
					}
				}
			}
			return sc;
		}
