void set_index(int16_t idx)
{
    index = idx;
}
void set_presample(int32_t pcm_16)
{
    predsample = pcm_16;
}

uint8_t ADPCM_Encode(int32_t sample)
{
    //  static int16_t  index = 0;
    //  static int32_t predsample = 0;
    uint8_t code = 0; //ima_adpcmå€¼
    uint16_t tmpstep = 0; //æ­¥é•¿
    int32_t diff = 0; // é¢„æµ‹è¾“å‡ºå€¼
    int32_t diffq = 0; // å½“å‰æ›´æ”¹å€¼
    uint16_t step = 0; // å½“å‰æ­¥é•¿
    step = StepSizeTable[index];
    /* 1. compute diff and record sign and absolut value */
    /* è®¡ç®—å·®å¼‚ï¼Œè®°å½•ç¬¦å·å’Œç»å¯¹å€¼ */
    diff = sample - predsample;
    if (diff < 0)
    {
        code = 8;
        diff = (-diff);
    }
    /* Note:
    ** This code *approximately* computes:
    **    code = diff*4/step;
    **    diffq = (code+0.5)*step/4;
    ** but in shift step bits are dropped. The net result of this is
    ** that even if you have fast mul/div hardware you cannot put it to
    ** good use since the fixup would be too expensive.
    */
    /* 3. quantize the diff into ADPCM code */
    /* 4. inverse quantize the code into a predicted diff */
    /* å°†å·®å¼‚é‡åŒ–ä¸ºADPCMä»£ç  */
    /* å°†ä»£ç åé‡åŒ–ä¸ºé¢„æµ‹çš„å·®å¼‚ */
    tmpstep = step;
    diffq = (step >> 3);
    if (diff >= tmpstep)
    {   
        //code += 0.5;
        code |= 0x04;
        diff -= tmpstep;
        diffq += step;
    }
    tmpstep = tmpstep >> 1;
    if (diff >= tmpstep)
    {
        //code += 0.5;
        code |= 0x02;
        diff -= tmpstep;
        diffq += (step >> 1);
    }
    tmpstep = tmpstep >> 1;
    if (diff >= tmpstep)
    {
        //code += 0.5;
        code |= 0x01;
        diffq += (step >> 2);
    }
    /* 5. fixed predictor to get new predicted sample*/
    /* å›ºå®šçš„é¢„æµ‹å¾—åˆ°æ–°çš„é¢„æµ‹æ ·æœ¬ */
    if (code & 8)
    {
        predsample -= diffq;
    }
    else
    {
        predsample += diffq;
    }
    /* check for overflow*/
    /* æº¢å‡ºæ£€æŸ¥*/
    if (predsample > 32767)
    {
        predsample = 32767;
    }
    else if (predsample < -32768)
    {
        predsample = -32768;
    }
    /* 6. find new stepsize index */
    /* æ‰¾å‡ºæ–°çš„ä¸‹æ ‡ç´¢å¼• */
    index += IndexTable[code];
    /* check for overflow*/
    /* æ£€æŸ¥æº¢å‡º */
    if (index <0)
    {
        index = 0;
    }
    else if (index > 88)
    {
        index = 88;
    }
    /* 8. return new ADPCM code*/
    /* è¿”å›adpcm å€¼*/
    //return (code);
    return (code & 0x0f);
}

int adpcm_encode_block(uint8_t *p_dst, uint8_t *p_src, uint32_t len)
{
    int length = 0;
    int16_t *p_pcm;
    uint32_t i;
    uint8_t tmp;
    p_pcm = (int16_t*)p_src;
    set_presample(*p_pcm);  //ç¬¬ä¸€ä¸ªä¸å‹ç¼©
    *(int16_t*)p_dst = *p_pcm++;    //ä¿å­˜ç¬¬ä¸€ä¸ªé‡‡æ ·å€¼
    p_dst += 2;
    len--;
    *p_dst++ = index;       //ä¿å­˜index
    *p_dst++ = 0;           //ä¿ç•™å­—èŠ‚  =0
    length += 8;
    int jj = 0;
    for (i = 0; i<(len >> 1); i++)
    {
        tmp = ADPCM_Encode(*p_pcm++);
        jj++;
        tmp |= ADPCM_Encode(*p_pcm++) << 4;
        jj++;
        *p_dst++ = tmp;
        length++;
    }
    return length / 2;
}

