        {
            for(int j=0;j&lt;num-i;j++)
            {
                Console.Write("   ");
            }
            for(int j=0;j&lt;=i;j++)
            {
                if(j==0||j==i)
                {
                    arr[i,j]=1;
                }
                else
                {
                    arr[i,j]=arr[i-1,j]+arr[i-1,j-1];
                }
                Console.Write(arr[i,j].ToString()+"      ");
            }
            Console.WriteLine();
        }
        Console.ReadKey();
    }
}
