	, &amp;#96;询盘表&amp;#96;.&amp;#96;来源&amp;#96;
	, count(&amp;#96;询盘表&amp;#96;.id) &amp;#96;询盘&amp;#96;
	, sum(case when &amp;#96;询盘表&amp;#96;.&amp;#96;有效否&amp;#96; &amp;#61; &amp;#39;有&amp;#39; then 1 else 0 end) as  &amp;#96;有效&amp;#96;
FROM &amp;#96;询盘表&amp;#96; 
GROUP BY MONTH(&amp;#96;询盘表&amp;#96;.&amp;#96;询盘日期&amp;#96;),  &amp;#96;询盘表&amp;#96;.&amp;#96;来源&amp;#96;&lt;/code&gt;&lt;/pre&gt;

