&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; &lt;span class="hljs-built_in"&gt;num&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;2&lt;/span&gt;;
&lt;span class="hljs-keyword"&gt;switch&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;num&lt;/span&gt;)
