&lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;:  &lt;span class="hljs-built_in"&gt;num&lt;/span&gt;&amp;#43;&amp;#43;;   &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;num&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;5&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
&lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-number"&gt;2&lt;/span&gt;:  &lt;span class="hljs-built_in"&gt;num&lt;/span&gt;&amp;#43;&amp;#43;;   &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;num&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;5&lt;/span&gt;) &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;

