        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Score &amp;lt; &lt;span class="hljs-number"&gt;90&lt;/span&gt; &amp;amp;&amp;amp; Score &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;80&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Score&amp;#61;%d,Grade&amp;#61;B&amp;#34;&lt;/span&gt;,Score);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (Score &amp;lt; &lt;span class="hljs-number"&gt;80&lt;/span&gt; &amp;amp;&amp;amp; Score &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;70&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Score&amp;#61;%d,Grade&amp;#61;C&amp;#34;&lt;/span&gt;,Score);
