                deck[i * ranks.length &amp;#43; j] &amp;#61; ranks[j] &amp;#43; suits[i];
            }
        }
        &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println(&amp;#34;The original deck is arranged as below:&amp;#34;);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; deck.length; i&amp;#43;&amp;#43;) {
            &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.print(deck[i]&amp;#43;&amp;#34; &amp;#34;);
        }
        &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println();

        Random random &amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; Random();

        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; deck.length; i&amp;#43;&amp;#43;) {
            &lt;span class="hljs-type"&gt;int&lt;/span&gt; r &amp;#61; random.nextInt(&lt;span class="hljs-number"&gt;51&lt;/span&gt;);
            String &lt;span class="hljs-keyword"&gt;temp&lt;/span&gt; &amp;#61; deck[i];
            deck[i] &amp;#61; deck[r];
            deck[r] &amp;#61; &lt;span class="hljs-keyword"&gt;temp&lt;/span&gt;;
        }
        &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println(&amp;#34;The shuffled deck is arranged as below:&amp;#34;);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; deck.length; i&amp;#43;&amp;#43;) {
            &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.print(deck[i]&amp;#43;&amp;#34; &amp;#34;);
        }
        &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println();

    }
}

