&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; System;
&lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; &lt;span class="hljs-title"&gt;ConsoleApplication1&lt;/span&gt;
{
    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Map&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Row { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Column { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;ShowMap&lt;/span&gt;(&lt;span class="hljs-params"&gt;Hero hero&lt;/span&gt;)&lt;/span&gt;
        {
            Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;地图显示&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;var&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt;&amp;#61; Row; i&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;var&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; j &amp;lt;&amp;#61; Column; j&amp;#43;&amp;#43;)
                {
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (i &amp;#61;&amp;#61; hero.Row &amp;amp;&amp;amp; j &amp;#61;&amp;#61; hero.Column) Console.Write(&lt;span class="hljs-string"&gt;&amp;#34;1&amp;#34;&lt;/span&gt;);
                    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; Console.Write(&lt;span class="hljs-string"&gt;&amp;#34;0&amp;#34;&lt;/span&gt;);
                }
                Console.WriteLine();
            }
        }
    }

    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;HM&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;string&lt;/span&gt; Name { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 血量&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Blood { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 最大攻击输出&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Max { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;bool&lt;/span&gt; &lt;span class="hljs-title"&gt;Attack&lt;/span&gt;(&lt;span class="hljs-params"&gt;HM hm&lt;/span&gt;)&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; s &amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;【&amp;#34;&lt;/span&gt;&amp;#43;Name&amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;&amp;#xff0c;血量&amp;#xff1a;&amp;#34;&lt;/span&gt; &amp;#43; Blood &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;】攻击【&amp;#34;&lt;/span&gt; &amp;#43; hm.Name&amp;#43;&lt;span class="hljs-string"&gt;&amp;#34;&amp;#xff0c;血量&amp;#xff1a;&amp;#34;&lt;/span&gt;&amp;#43;hm.Blood&amp;#43;&lt;span class="hljs-string"&gt;&amp;#34;】&amp;#xff0c;掉血&amp;#xff1a;&amp;#34;&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; r &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Random(Guid.NewGuid().GetHashCode());
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; v &amp;#61; r.Next(&lt;span class="hljs-number"&gt;0&lt;/span&gt;, Max &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
            hm.Blood -&amp;#61; v;
            s &amp;#43;&amp;#61; v &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;【&amp;#34;&lt;/span&gt; &amp;#43; hm.Name &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;】当前血量&amp;#xff1a;&amp;#34;&lt;/span&gt; &amp;#43; hm.Blood;
            Console.WriteLine(s);
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; hm.Blood &amp;lt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        }
    }
    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Hero&lt;/span&gt;:&lt;span class="hljs-title"&gt;HM&lt;/span&gt;
    {
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 玩家位置所在行&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Row { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 玩家位置所在列&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; Column { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;bool&lt;/span&gt; &lt;span class="hljs-title"&gt;Move&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;span class="hljs-built_in"&gt;string&lt;/span&gt; direction,Map map&lt;/span&gt;)&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;switch&lt;/span&gt; (direction)
            {
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;w&amp;#34;&lt;/span&gt;: 
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;s&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ((direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;w&amp;#34;&lt;/span&gt; &amp;amp;&amp;amp; Row &amp;lt; &lt;span class="hljs-number"&gt;2&lt;/span&gt;) || (direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;s&amp;#34;&lt;/span&gt; &amp;amp;&amp;amp; Row &amp;gt;&amp;#61; map.Row)) { Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;超出地图范围&amp;#xff0c;请重新输入移动命令&amp;#34;&lt;/span&gt;); &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;false&lt;/span&gt;; }
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;w&amp;#34;&lt;/span&gt;) Row--;
                    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; Row&amp;#43;&amp;#43;;
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;a&amp;#34;&lt;/span&gt;: 
                &lt;span class="hljs-keyword"&gt;case&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;#34;d&amp;#34;&lt;/span&gt;:
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; ((direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;a&amp;#34;&lt;/span&gt; &amp;amp;&amp;amp; Column &amp;lt; &lt;span class="hljs-number"&gt;2&lt;/span&gt;) || (direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;d&amp;#34;&lt;/span&gt; &amp;amp;&amp;amp; Column &amp;gt;&amp;#61; map.Column)) { Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;超出地图范围&amp;#xff0c;请重新输入移动命令&amp;#34;&lt;/span&gt;); &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;false&lt;/span&gt;; }
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (direction &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;a&amp;#34;&lt;/span&gt;) Column--;
                    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; Column&amp;#43;&amp;#43;;
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
            }
            map.ShowMap(&lt;span class="hljs-keyword"&gt;this&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
        }
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-built_in"&gt;bool&lt;/span&gt; &lt;span class="hljs-title"&gt;Escape&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; r &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Random();
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; v &amp;#61; r.Next(&lt;span class="hljs-number"&gt;1&lt;/span&gt;, &lt;span class="hljs-number"&gt;11&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (v &amp;lt; &lt;span class="hljs-number"&gt;4&lt;/span&gt;)
            {
                Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;战斗结束&amp;#xff0c;玩家跑了&amp;#34;&lt;/span&gt;);
                &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
            }
            Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;很遗憾&amp;#xff0c;逃跑失败&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;false&lt;/span&gt;;
        }
    }
    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Monster&lt;/span&gt;:&lt;span class="hljs-title"&gt;HM&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; Monster[] monsters &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Monster[] {
            &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Monster{ Name&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;钢铁侠&amp;#34;&lt;/span&gt;,Blood&amp;#61;&lt;span class="hljs-number"&gt;30&lt;/span&gt;,Max&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;},
            &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Monster{ Name&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;蜘蛛侠&amp;#34;&lt;/span&gt;,Blood&amp;#61;&lt;span class="hljs-number"&gt;20&lt;/span&gt;,Max&amp;#61;&lt;span class="hljs-number"&gt;6&lt;/span&gt;},
            &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Monster{ Name&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;雷神&amp;#34;&lt;/span&gt;,Blood&amp;#61;&lt;span class="hljs-number"&gt;40&lt;/span&gt;,Max&amp;#61;&lt;span class="hljs-number"&gt;15&lt;/span&gt;}};
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; 生成怪物&lt;/span&gt;
        &lt;span class="hljs-comment"&gt;&lt;span class="hljs-doctag"&gt;///&lt;/span&gt; &lt;span class="hljs-doctag"&gt;&amp;lt;/summary&amp;gt;&lt;/span&gt;&lt;/span&gt;
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; Monster &lt;span class="hljs-title"&gt;Create&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; r &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Random();
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; m &amp;#61; Monster.monsters[r.Next(&lt;span class="hljs-number"&gt;0&lt;/span&gt;, Monster.monsters.Length)];

            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Monster
            {
                Name &amp;#61; m.Name,
                Blood &amp;#61; m.Blood,
                Max &amp;#61; m.Max
            };
        }
    }
    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;Game&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; Map map { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; Hero hero { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }
        &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; Monster monster { &lt;span class="hljs-keyword"&gt;get&lt;/span&gt;; &lt;span class="hljs-keyword"&gt;set&lt;/span&gt;; }

        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-built_in"&gt;string&lt;/span&gt; &lt;span class="hljs-title"&gt;GetAttacCmd&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;
        {
            Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;进入战斗系统&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;&amp;#61;\n&amp;#34;&lt;/span&gt;
                &amp;#43;&lt;span class="hljs-string"&gt;&amp;#34;选择操作\n&amp;#34;&lt;/span&gt;
                &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;1&amp;#xff1a;攻击\n&amp;#34;&lt;/span&gt;
                &amp;#43; &lt;span class="hljs-string"&gt;&amp;#34;其他&amp;#xff1a;逃跑&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;var&lt;/span&gt; cmd &amp;#61; Console.ReadLine();
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; cmd;
        }
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-keyword"&gt;private&lt;/span&gt; &lt;span class="hljs-built_in"&gt;string&lt;/span&gt; &lt;span class="hljs-title"&gt;GetMoveCmd&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;
        {
            Console.WriteLine(&lt;span class="hljs-string"&gt;&amp;#34;请输入w/a/s/d移动玩家&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (&lt;span class="hljs-literal"&gt;true&lt;/span&gt;)
