def isOdd(d):
	&amp;#39;&amp;#39;&amp;#39;
	判断第 d 周是奇数还是偶数
	:param d:
	:type d:
	:return: 偶数&amp;#xff1a; true&amp;#xff1b; 奇数 false
	:rtype:
	&amp;#39;&amp;#39;&amp;#39;
	if d%2 &amp;#61;&amp;#61; 0:
		return True
	else:
		return False


def eatOld(a, old, c):
	&amp;#39;&amp;#39;&amp;#39;
	年纪最大的被吃
	:param a:
	:type a:
	:param old:
	:type old:
	:param c:
	:type c:
	:return:
	:rtype:
	&amp;#39;&amp;#39;&amp;#39;
	if  a[old] - c &amp;gt;&amp;#61;0:
		a[old] &amp;#61; a[old] -c
		return a
	else:
		a[old] &amp;#61; 0
		return eatOld(a, old &amp;#43; 1, c-a[old])

def eatYoung(a, old, c):
	&amp;#39;&amp;#39;&amp;#39;
	年纪最小的被吃
	:param a:
	:type a:
	:param old:
	:type old:
	:param c:
	:type c:
	:return:
	:rtype:
	&amp;#39;&amp;#39;&amp;#39;
	if  a[old] - c &amp;gt;&amp;#61;0:
		a[old] &amp;#61; a[old] -c
		return a
	else:
		a[old] &amp;#61; 0
		return eatYoung(a, old - 1, c-a[old])

def eated(a, c):
	&amp;#39;&amp;#39;&amp;#39;
	偶数周&amp;#xff0c;被吃掉的兔子&amp;#xff0c;&amp;#xff0c; 更新a
	:param a:
	:type a:
	:param c:
	:type c:
	:return:
	:rtype:
	&amp;#39;&amp;#39;&amp;#39;
	total &amp;#61; sum(a)
	if total &amp;gt; 2*c:
		a &amp;#61; eatOld(a, 0, c)
		a &amp;#61; eatYoung(a, -1, c)
	else:
		for i in range(len(a)):
			a[i] &amp;#61; 0
	return a

def born(a, bornTrue):
	&amp;#39;&amp;#39;&amp;#39;
	每周新增的兔子
	小于3 或者大于3的偶数周&amp;#xff0c;新生兔子0 更新a
