int narcissistic( int number )
{
    int h,x=number,sum=0;
    while(x>0)
    {
        h=x%10;
        x=x/10;
        sum=sum+h*h*h;
    }
    if(sum==number)
        return 1;
    else
        return 0;
}
void PrintN( int m, int n )
{
    int i;
    for (i=m+1;i<n;i++)
    {
        if(narcissistic(i)==1)
            printf ("%d\n",i);
    }
}

