#include <stdio.h>  
#include <math.h>
double fact(int n) {            //阶乘函数
    double p=1;
    for (int i = 1; i <= n; i++) {
        p = p * i;
    }
    return p;
}

int main() {
    double x,sum=1;
    scanf("%lf", &x);
    for(int i=1;pow(x,i)/fact(i)>= 0.00001;i++){
        sum = sum + pow(x, i) / fact(i);
        //printf("%.4lf\n", pow(x, i) / fact(i));
    }
    printf("%.4lf\n",sum);
    return 0;
}

