            kvMonster&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;m&lt;/span&gt;.&lt;span class="hljs-identifier"&gt;type&lt;/span&gt;]&lt;/span&gt;.&lt;span class="hljs-constructor"&gt;Add(&lt;span class="hljs-params"&gt;m&lt;/span&gt;)&lt;/span&gt;;
        }
        public void &lt;span class="hljs-constructor"&gt;RemoveMonster(Monster &lt;span class="hljs-params"&gt;m&lt;/span&gt;)&lt;/span&gt;
        {
            kvMonster&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;m&lt;/span&gt;.&lt;span class="hljs-identifier"&gt;type&lt;/span&gt;]&lt;/span&gt;.&lt;span class="hljs-constructor"&gt;Remove(&lt;span class="hljs-params"&gt;m&lt;/span&gt;)&lt;/span&gt;;
        }
        public void &lt;span class="hljs-constructor"&gt;ShowMonster()&lt;/span&gt;
        {
            foreach (var kp &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; kvMonster)
